require('LiveScript');

var zappa = require('zappajs');

var Server = require('./server').server.Server;


function run(routes, options, logger) {
	var server = Server({
		system: zappa,
		routes: routes,
		options: options
	}, logger);

	server.run();

	return server;
};



exports.rpc = require('rpc');
exports.server = require('server');

exports.run = run;
