var Logger = require('tester').logger.Logger;
var httpServer = require('./');



var $logger = new Logger();



// -----------------------------------------------------------------------------

var routes = [
	'ping'
];

var options = {
	network: {ports: {preferred: 3000}}
};

var logger = {
	log: console.log,
	error: console.error,
	warn: console.error
};


// -----------------------------------------------------------------------------

$logger.sep('Main');
$logger.on();

var server = httpServer.run(routes, options, logger);

$logger.log(server);
$logger.log(server.port);

server.on('start', function(server, port) {
	$logger.log(port);
	$logger.log(server.port);
});
